export class InfoWindow extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hovered: false,
      hoveredType: "",
      textScrollColumn: 1,
      imageCarouselScaled: false,
    };
    this.openPageVideoRef = React.createRef();
    this.windowContainerRef = React.createRef();
    this.windowButtonsRef = React.createRef();
    this.imageContainerRef = React.createRef();
    this.windowContentRef = React.createRef();
    this.blockOverlayRef = React.createRef();
    this.blockOverlayMovieRef = React.createRef();
    this.windowMovieContainerRef = React.createRef();
    this.openPageMovieRef = React.createRef();
    this.movieRef = React.createRef();
  }
  mouseEnterHandler = (event, type) => {
    this.setState({ hovered: true, hoveredType: type });
    //  this.props.hoveredChanged(true);
    switch (type) {
      case "close":
        break;
      default:
        break;
    }
  };

  mouseLeaveHandler = () => {
    this.clearTimer();
    this.setState({ hovered: false });
    // this.props.hoveredChanged(false);
  };

  onTimeout = () => {
    switch (this.state.hoveredType) {
      case "image-carousel-scaled":
        let currentState = this.state.imageCarouselScaled;
        this.setState({
          imageCarouselScaled: !currentState,
          hovered: false,
        });
        break;
      case "close":
        this.closeWindow();
        console.log("close");
        break;
      case "reset":
        this.closeWindow();
        this.props.changePageNumber(0);
        break;
      case "play":
        this.movieRef.current.play();
        break;
      case "pause":
        this.movieRef.current.pause();
        break;
      case "scroll":
        let activeColumn = plants[this.props.plantIndex].textColumns;

        if (this.state.textScrollColumn < activeColumn) {
          let columnCurrent = this.state.textScrollColumn + 1;
          this.setState({
            textScrollColumn: columnCurrent,
          });
        } else {
          this.setState({
            textScrollColumn: 1,
          });
        }
        this.setState({
          hovered: false,
        });
        this.clearTimer();
        break;
      default:
    }
  };

  closeWindow() {
    if (this.props.plantOpen || this.props.movieOpen) {
      this.windowContainerRef.current.classList.remove("window-container-animation-class1");
      this.windowContainerRef.current.classList.add("window-container-animation-class2");
      this.imageContainerRef.current.classList.remove("image-container-animation");
      this.blockOverlayRef.current.classList.remove("block-overlay-animation");

      this.windowMovieContainerRef.current.classList.remove("window-container-animation-class1");
      this.windowMovieContainerRef.current.classList.add("window-container-animation-class2");
      this.blockOverlayRef.current.classList.remove("block-overlay-animation");
      this.blockOverlayMovieRef.current.classList.remove("block-overlay-animation");
      this.movieRef.current.currentTime = 0;

      this.movieRef.current.pause();
    }
    let plantClose = { plantIndex: this.props.plantIndex, open: false };
    this.props.mouseEnterPlant(plantClose);
    this.props.mouseEnterMovie(false);

    this.setState({
      textScrollColumn: 1,
      imageCarouselScaled: false,
    });
  }

  clearTimer = () => {
    clearTimeout(this.timer);
  };

  componentWillUnmount() {
    this.clearTimer();
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.hovered !== prevProps.hovered) {
      if (this.state.hovered) {
        let hoverTime;
        if (this.state.hoveredType === "close") {
          hoverTime = 1200;
        } else if (this.state.hoveredType === "scroll") {
          hoverTime = 500;
        } else if (this.state.hoveredType === "reset") {
          hoverTime = 120000;
        } else {
          hoverTime = 1000;
        }
        this.timer = setTimeout(this.onTimeout, hoverTime);
      } else {
        this.clearTimer();
      }
    }

    if (this.props.page !== prevProps.page) {
      this.closeWindow();
    }
    if (this.props.plantOpen !== prevProps.plantOpen) {
      if (this.props.plantOpen) {
        this.windowContainerRef.current.classList.remove("window-container-animation-class1");
        this.windowContainerRef.current.classList.remove("window-container-animation-class2");
        this.windowContainerRef.current.classList.add("window-container-animation-class1");

        //this.imageContainerRef.current.classList.add("image-container-animation");
        this.blockOverlayRef.current.classList.add("block-overlay-animation");
        this.openPageVideoRef.current.currentTime = 0;
        this.openPageVideoRef.current.play();
      }
    }

    if (this.props.movieOpen !== prevProps.movieOpen) {
      if (this.props.movieOpen) {
        this.windowMovieContainerRef.current.classList.remove("window-container-animation-class1");
        this.windowMovieContainerRef.current.classList.remove("window-container-animation-class2");
        this.windowMovieContainerRef.current.classList.add("window-container-animation-class1");
        this.openPageMovieRef.current.currentTime = 0;
        this.openPageMovieRef.current.play();
        this.movieRef.current.currentTime = 0;
        this.movieRef.current.play();

        this.blockOverlayMovieRef.current.classList.add("block-overlay-animation");
      }
    }

    // if (this.props.reset !== prevProps.reset) {
    //   if (this.props.plantOpen || this.props.movieOpen) {
    //     this.closeWindow();
    //   }
    // }
  }

  render() {
    const imageNumbers = [1, 2, 3, 4, 1];

    return (
      <div>
        <div
          className={"window-container"}
          ref={this.windowContainerRef}
          key={`windowKey${this.props.hoveredGlobal}`}
        >
          <video muted ref={this.openPageVideoRef}>
            <source src={openPage} type="video/mp4" />
          </video>
          <div className={"window-content"} ref={this.windowContentRef}>
            <div className="content content-left">
              <div
                className={`image-container plant-image-container `}
                ref={this.imageContainerRef}
                style={{ transform: this.state.imageCarouselScaled ? "scale(1.5)" : "scale(1)" }}
                onMouseEnter={(event) => this.mouseEnterHandler(event, "image-carousel-scaled")}
                onMouseLeave={this.mouseLeaveHandler}
              >
                <div className="image-frame"></div>
                <div className="image-carousel-overflow">
                  <div className="image-carousel plant-image-carousel">
                    {imageNumbers.map((imageNumber, index) => {
                      let imageUrl = require(`../images/plants/${
                        plants[this.props.plantIndex].imageName
                      }${imageNumber}.jpg`);

                      return (
                        <div
                          key={"image" + index}
                          style={{
                            backgroundImage: `url(${imageUrl})`,
                          }}
                          className="image"
                        ></div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </div>
            <div className="content content-right">
              <div className="text-container">
                <h1>{plants[this.props.plantIndex].name}</h1>
                <div className="text-carousel">
                  <p
                    style={{
                      transform: `translateX(calc(${
                        this.state.textScrollColumn - 1
                      }*(-100% - 4rem)))`,
                    }}
                  >
                    {plants[this.props.plantIndex].description}
                  </p>
                </div>
                <div className={`window-buttons`} ref={this.windowButtonsRef}>
                  <div
                    className="btn btn-close"
                    onMouseEnter={(event) => this.mouseEnterHandler(event, "close")}
                    onMouseLeave={this.mouseLeaveHandler}
                  >
                    <div className="hover-box hover-box-left"></div>
                  </div>
                  <div
                    className="btn btn-scroll"
                    onMouseEnter={(event) => this.mouseEnterHandler(event, "scroll")}
                    onMouseLeave={this.mouseLeaveHandler}
                  >
                    <div className="hover-box hover-box-right"></div>
                  </div>
                </div>
              </div>
            </div>
            <div className="block-overlay" ref={this.blockOverlayRef}></div>
          </div>
        </div>

        <div className={"window-movie-container"} ref={this.windowMovieContainerRef}>
          <video muted ref={this.openPageMovieRef}>
            <source src={openPageMovie} type="video/mp4" />
          </video>
          <div className="window-content">
            <div className="video-screen">
              <video ref={this.movieRef}>
                <source src={movie} type="video/mp4" />
              </video>
            </div>
          </div>
          <div
            className={`window-buttons window-buttons-movie `}
            key={`windowMovieKey${this.props.hoveredGlobal}`}
          >
            <div
              className="btn btn-play"
              onMouseEnter={(event) => this.mouseEnterHandler(event, "play")}
              onMouseLeave={this.mouseLeaveHandler}
            >
              <div className="white-box"></div>
            </div>
            <div
              className="btn btn-pause"
              onMouseEnter={(event) => this.mouseEnterHandler(event, "pause")}
              onMouseLeave={this.mouseLeaveHandler}
            >
              <div className="white-box"></div>
            </div>
            <div
              className="btn btn-close"
              onMouseEnter={(event) => this.mouseEnterHandler(event, "close")}
              onMouseLeave={this.mouseLeaveHandler}
            >
              <div className="white-box"></div>
            </div>
          </div>
          <div className="block-overlay" ref={this.blockOverlayMovieRef}></div>
        </div>
        {/* <div
          className="reset-box-container"
          onMouseEnter={(event) => this.mouseEnterHandler(event, "reset")}
          onMouseLeave={this.mouseLeaveHandler}
        >
          <div className="reset-box reset-box-left"></div>
          <div className="reset-box reset-box-right"></div>
          <div className="reset-box reset-box-top"></div>
          <div className="reset-box reset-box-bottom"></div>
        </div> */}
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    page: state.changePageNumber.page,
    plantIndex: state.mouseEnterPlant.plantIndex,
    plantOpen: state.mouseEnterPlant.plantOpen,
    movieOpen: state.mouseEnterMovie.movieOpen,
    hoveredGlobal: state.hoveredChanged.hoveredGlobal,
    reset: state.resetChanged.reset,
  };
};

const mapDispatchToProps = {
  changePageNumber,
  mouseEnterPlant,
  mouseEnterMovie,
  hoveredChanged,
};

export default connect(mapStateToProps, mapDispatchToProps)(InfoWindow);
